import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Llamada a la API
    fetch('https://dragonball-api.com/api/characters')
      .then(res => res.json())
      .then(data => {
        console.log(data); // <--- VER EN CONSOLA cómo llega la info

        // Detecta si la API devuelve "data" dentro de un objeto
        if (data.data && Array.isArray(data.data)) {
          setCharacters(data.data);
        } 
        // Si devuelve un array directamente
        else if (Array.isArray(data)) {
          setCharacters(data);
        } 
        // Si no hay personajes
        else {
          setCharacters([]);
        }

        setLoading(false);
      })
      .catch(err => {
        console.error('Error al cargar personajes:', err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Cargando personajes...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Dragon Ball Characters</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {characters.length > 0 ? (
          characters.map(char => (
            <div
              key={char.id}
              style={{
                margin: '10px',
                border: '1px solid gray',
                padding: '10px',
                textAlign: 'center',
                width: '180px',
              }}
            >
              <Link to={`/character/${char.id}`} style={{ textDecoration: 'none', color: 'black' }}>
                <img
                  src={char.image || 'https://via.placeholder.com/150'}
                  alt={char.name}
                  width="150"
                  height="150"
                />
                <h3>{char.name || 'Sin nombre'}</h3>
              </Link>
            </div>
          ))
        ) : (
          <p>No se encontraron personajes.</p>
        )}
      </div>
    </div>
  );
}

export default Home;
