import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    fetch('https://dragonball-api.com/api/characters')
      .then(res => res.json())
      .then(data => {
        // Asegurarse de que data.data sea un array
        if (data.data) {
          setCharacters(data.data);
        } else {
          setCharacters([]);
        }
      })
      .catch(err => console.log(err));
  }, []);

  return (
    <div>
      <h1>Dragon Ball Characters</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {Array.isArray(characters) && characters.map(char => (
          <div key={char.id} style={{ margin: '10px', border: '1px solid gray', padding: '10px' }}>
            <Link to={`/character/${char.id}`}>
              <img src={char.image} alt={char.name} width="150" />
              <h3>{char.name}</h3>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
