import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('https://dragonball-api.com/api/characters') // Endpoint de todos los personajes
      .then(res => res.json())
      .then(data => {
        console.log(data); // Para verificar la estructura del JSON
        // Ajusta aquí según la propiedad que contenga los personajes
        setCharacters(data.data || []); 
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Cargando personajes...</p>;

  return (
    <div>
      <h1>Dragon Ball Characters</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {Array.isArray(characters) && characters.length > 0 ? (
          characters.map(char => (
            <div
              key={char.id}
              style={{
                margin: '10px',
                border: '1px solid gray',
                padding: '10px',
                textAlign: 'center',
              }}
            >
              <Link to={`/character/${char.id}`}>
                <img src={char.image} alt={char.name} width="150" />
                <h3>{char.name}</h3>
              </Link>
            </div>
          ))
        ) : (
          <p>No se encontraron personajes.</p>
        )}
      </div>
    </div>
  );
}

export default Home;
